import { Button } from "bootstrap";
import React from "react";
import Firebase from "../../firebase";
import NavBarPrefix from "../NavBarPrefix";

const db = Firebase.database()
const Fid = Firebase.auth()
export default function NavBar() {

  const downloadTxtFile = async() => {


    let id = Fid.currentUser.uid
    let HTMLTxt, CSSTxt, JavascriptTxt
    await db.ref('/Users/'+id+'/html/test1/').once("value", snapshot => {
          if(snapshot.val()!==null){
            HTMLTxt = snapshot.val().value
          }
      }).catch((e)=>console.log(e))

      await db.ref('/Users/'+id+'/css/').once("value", snapshot => {
        if(snapshot.val()!==null){
          CSSTxt = snapshot.val().value
        }
          }).catch((e)=>console.log(e))

          await db.ref('/Users/'+id+'/javascript/').once("value", snapshot => {
            if(snapshot.val()!==null){
              JavascriptTxt = snapshot.val().value
            }
              }).catch((e)=>console.log(e))





    const element = document.createElement("a");

    const fileHTML = new Blob([HTMLTxt], {
      type: "text/plain"
    });
    element.href = URL.createObjectURL(fileHTML);
    element.download = "index.html";
    document.body.appendChild(element);
    element.click();
    const fileCSS = new Blob([HTMLTxt], {
      type: "text/plain"
    });
    element.href = URL.createObjectURL(fileCSS);
    element.download = "index.html";
    document.body.appendChild(element);
    element.click();
    const fileJavaScript = new Blob([HTMLTxt], {
      type: "text/plain"
    });
    element.href = URL.createObjectURL(fileJavaScript);
    element.download = "index.html";
    document.body.appendChild(element);
    element.click();



  };
  return (
    <div className="navbar p-3">
      <NavBarPrefix name="IDE" />
      <button onClick={()=>{
        // console.log('hi',Firebase.auth())
        Firebase.auth().signOut()
        .then(function(data) {
          console.log('data',data)
          // Sign-out successful.
        })
        .catch(function(error) {
          console.log('error',error)
          // An error happened
        });
      }} 
      style={{borderRadius:50,backgroundColor:'purple'}} 
       ><text style={{color:'#fff'}}>LOG OUT</text></button>


<button onClick={downloadTxtFile}>Download</button>

    </div>
  );
}
