        .catch(function(error) {
          console.log('error',error)
          // An error happened
        });
      }} 
      style={{borderRadius:50,backgroundColor:'purple'}} 
       ><text style={{color:'#fff'}}>LOG OUT</text></button>


<button onClick={downloadTxtFile}>Download</button>

    </div>
  );
}
