import { Button } from "bootstrap";
import React from "react";
import Firebase from "../../firebase";
import NavBarPrefix from "../NavBarPrefix";

const db = Firebase.database()
const Fid = Firebase.auth()
export default function NavBar() {

  const downloadTxtFile = () => {


    let id = Fid.currentUser.uid
    let HTMLTxt
        db.ref('/Users/'+id+'/html/test1/').once("value", snapshot => {
          console.log('hi',snapshot.val().value)
          if(snapshot.val()!==null){
            HTMLTxt = snapshot.val().value
          }
      }).catch((e)=>console.log(e))

      // db.ref('/Users/'+id+'/css/').once("value", snapshot => {
      //   if(snapshot.val()!==null){

      //   setCSS(snapshot.val().value)
      //   }
      //     }).catch((e)=>console.log(e))

      //     db.ref('/Users/'+id+'/javascript/').once("value", snapshot => {
      //       if(snapshot.val()!==null){

      //       setJs(snapshot.val().value)
      //       }
      //         }).catch((e)=>console.log(e))




console.log('HTMLTxt',HTMLTxt)


    const element = document.createElement("a");
    const file = new Blob([HTMLTxt], {
      type: "text/plain"
    });
    // const file = new Blob([JSON.stringify(HTMLTxt, null, 2)], {type : 'application/json'});

    element.href = URL.createObjectURL(file);
    element.download = "index.html";
    document.body.appendChild(element);
    element.click();



  };
  return (
    <div className="navbar p-3">
      <NavBarPrefix name="IDE" />
      <button onClick={()=>{
        // console.log('hi',Firebase.auth())
        Firebase.auth().signOut()
        .then(function(data) {
          console.log('data',data)
          // Sign-out successful.
        })
        .catch(function(error) {
          console.log('error',error)
          // An error happened
        });
      }} 
      style={{borderRadius:50,backgroundColor:'purple'}} 
       ><text style={{color:'#fff'}}>LOG OUT</text></button>


<button onClick={downloadTxtFile}>Download</button>

    </div>
  );
}
