import React from "react";
import CodeEditor from "./CodeEditor";
import "bootstrap/dist/css/bootstrap.min.css";
import { initialHTML, initialCSS, initialJS } from "./InitialValues";
import { useState, useEffect } from "react";
import ColumnResizer from "react-column-resizer";
import "./App.css";
import NavBar from "./NavBar";
import Firebase from "../../firebase";


const db = Firebase.database()

export function App() {
  const [htmlValue, setHtml] = useState(initialHTML);
  const [cssValue, setCSS] = useState(initialCSS);
  const [jsValue, setJs] = useState(initialJS);
  const [Code, setCode] = useState(`
<html>
<body>${htmlValue}</body>
<style>${cssValue}</style>
<script>${jsValue}</script>
</html>`);

  useEffect(() => {


    db.ref('/Users/html/test1/').once("value", snapshot => {
      console.log(snapshot.val().value)
setHtml(snapshot.val().value)
  }).catch((e)=>console.log(e))

  db.ref('/Users/css/test1/').once("value", snapshot => {
    setCSS(snapshot.val().value)
      }).catch((e)=>console.log(e))

      db.ref('/Users/javascript/test1/').once("value", snapshot => {
        setJs(snapshot.val().value)
          }).catch((e)=>console.log(e))
// setCode(`<html>
//   <body>${htmlValue}</body>
//   <style>${cssValue}</style>
//   <script>${jsValue}</script>
//   </html>`)


    const timeout = setTimeout(() => {
      setCode(`
      <html>
      <body>${htmlValue}</body>
      <style>${cssValue}</style>
      <script>${jsValue}</script>
      </html>`);
    }, 250);
    return () => clearTimeout(timeout);
  }, [htmlValue, cssValue, jsValue]);



  return (
    <>
      <NavBar className="navbar p-3" />
      <div className="row">
         <div></div>
      <center>

        <div className="pane top-pane">
          <table>
            <tbody>
              <tr>
                <CodeEditor
                  displayName="HTML"
                  lang="html"
                  value={htmlValue}
                  setValue={setHtml}
                />

                <ColumnResizer className="columnResizer resizer" minWidth={0} />

                <CodeEditor
                  displayName="CSS"
                  lang="css"
                  value={cssValue}
                  setValue={setCSS}
                />

                <ColumnResizer className="columnResizer resizer" minWidth={0} />

                <CodeEditor
                  displayName="Javascript"
                  lang="javascript"
                  value={jsValue}
                  setValue={setJs}
                />
              </tr>
            </tbody>
          </table>
        </div>

{/* { console.log(Code) } */}
        <div className="outputScreen">
          <iframe
            srcDoc={Code}
            title="output"
            width="100%"
            height="100%"
            frameBorder="0"
            sandbox="allow-scripts"
            loading="lazy"
          ></iframe>
        </div>
      </center>

      </div>
    </>
  );
}
