import React from "react";
import CodeEditor from "./CodeEditor";
import "bootstrap/dist/css/bootstrap.min.css";
import { initialHTML, initialCSS, initialJS } from "./InitialValues";
import { useState, useEffect } from "react";
import ColumnResizer from "react-column-resizer";
import "./App.css";
import NavBar from "./NavBar";

import useLocalStorage from "../../hooks/useLocalStorage";

import firebase from 'firebase/app';
import 'firebase/database'
import "firebase/auth";
const firebaseConfig = {
    apiKey: "AIzaSyBHurO_5lzS-1wYyGKoBqzU3Y02Gdamjr4",
    authDomain: "codeeditor-315d2.firebaseapp.com",
    projectId: "codeeditor-315d2",
    storageBucket: "codeeditor-315d2.appspot.com",
    messagingSenderId: "565594386888",
    appId: "1:565594386888:web:f878168956a881bb27d0fd"
}

const Firebase = firebase.initializeApp(firebaseConfig);

const db = Firebase.database()
const auth = firebase.auth()

export function App() {
  const [htmlValue, setHtml] = useLocalStorage("html", initialHTML);
  const [cssValue, setCSS] = useLocalStorage("css", initialCSS);
  const [jsValue, setJs] = useLocalStorage("js", initialJS);
  const [Code, setCode] = useState(`
<html>
<body>${htmlValue}</body>
<style>${cssValue}</style>
<script>${jsValue}</script>
</html>`);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCode(`
      <html>
      <body>${htmlValue}</body>
      <style>${cssValue}</style>
      <script>${jsValue}</script>
      </html>`);
    }, 250);
    return () => clearTimeout(timeout);
  }, [htmlValue, cssValue, jsValue]);

  return (
    <>
      <NavBar className="navbar p-3" />
      <center>
        <div className="pane top-pane">
          <table>
            <tbody>
              <tr>
                <CodeEditor
                  displayName="HTML"
                  lang="html"
                  value={htmlValue}
                  setValue={setHtml}
                />

                <ColumnResizer className="columnResizer resizer" minWidth={0} />

                <CodeEditor
                  displayName="CSS"
                  lang="css"
                  value={cssValue}
                  setValue={setCSS}
                />

                <ColumnResizer className="columnResizer resizer" minWidth={0} />

                <CodeEditor
                  displayName="Javascript"
                  lang="javascript"
                  value={jsValue}
                  setValue={setJs}
                />
              </tr>
            </tbody>
          </table>
        </div>

        <div className="outputScreen">
          <iframe
            srcDoc={Code}
            title="output"
            width="100%"
            height="100%"
            frameBorder="0"
            sandbox="allow-scripts"
            loading="lazy"
          ></iframe>
        </div>
      </center>
    </>
  );
}
